mapper.update:
update fc_comment
set replys = replys + 1
where id = #{id}

update "fc_comment"
user_id = #{userId,jdbcType=INTEGER}, post_id = #{postId,jdbcType=INTEGER}, replys = #{replys,jdbcType=INTEGER}, content = #{content,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, flag = #{flag,jdbcType=SMALLINT}, likes = #{likes,jdbcType=INTEGER}, top_order = #{topOrder,jdbcType=INTEGER}, remark = #{remark,jdbcType=VARCHAR},
where id = #{id,jdbcType=INTEGER}
