mapper.update:
update fc_post
set comments = comments + 1
where id = #{id}

update fc_post
set comments = comments - 1 - (select replys from fc_comment where fc_comment.id = #{id})
where id = #{postId}

update fc_post
set comments = comments - 1
where id = #{id}

update fc_post
set browses = browses + 1
where id = #{id}

update fc_post
set likes = likes + 1
where id = #{id}

update fc_post
set collects = collects + 1
where id = #{id}

update fc_post
set collects = collects - 1
where id = #{id}

update "fc_deal"
user_id = #{userId,jdbcType=INTEGER}, title = #{title,jdbcType=VARCHAR}, content = #{content,jdbcType=VARCHAR}, "state" = #{state,jdbcType=VARCHAR}, price = #{price,jdbcType=NUMERIC}, original_price = #{originalPrice,jdbcType=NUMERIC}, want = #{want,jdbcType=INTEGER}, release_time = #{releaseTime,jdbcType=TIMESTAMP}, "type" = #{type,jdbcType=VARCHAR}, likes = #{likes,jdbcType=INTEGER}, comments = #{comments,jdbcType=INTEGER}, collects = #{collects,jdbcType=INTEGER}, flag = #{flag,jdbcType=INTEGER}, top_order = #{topOrder,jdbcType=INTEGER}, create_time = #{createTime,jdbcType=TIMESTAMP}, "location" = #{location,jdbcType=VARCHAR}, imgs = #{imgs,jdbcType=VARCHAR}, coordinate = #{coordinate,jdbcType=VARCHAR}, browses = #{browses,jdbcType=INTEGER},
where id = #{id,jdbcType=INTEGER}
