mapper.update:
update fc_post
set comments = comments + 1
where id = #{id}

update fc_post
set comments = comments - 1 - (select replys from fc_comment where fc_comment.id = #{id})
where id = #{postId}

update fc_post
set comments = comments - 1
where id = #{id}

update "fc_post"
user_id = #{userId,jdbcType=INTEGER}, title = #{title,jdbcType=VARCHAR}, content = #{content,jdbcType=VARCHAR}, "state" = #{state,jdbcType=VARCHAR}, release_time = #{releaseTime,jdbcType=TIMESTAMP}, "type" = #{type,jdbcType=VARCHAR}, likes = #{likes,jdbcType=INTEGER}, comments = #{comments,jdbcType=INTEGER}, collects = #{collects,jdbcType=INTEGER}, flag = #{flag,jdbcType=INTEGER}, top_order = #{topOrder,jdbcType=INTEGER}, create_time = #{createTime,jdbcType=TIMESTAMP},
where id = #{id,jdbcType=INTEGER}
